        type: 'variable',
        matchRegexp: /^\{\{(nickname)\}\}/,
        className: 'cm-keyword',
        text: 'Peter'
      }, {
        type: 'variable',
        matchRegexp: /^\{\{(product)\}\}/,
        className: 'cm-keyword',
        text: 'Product Name'
      }, {
