const container = document.getElementById('root');

const editor = new TemplateEditor(container, {
  initialValue: 'Dear {{nickname}}...',
  placeholders: [
    {
      type: 'variable',
      matchRegexp: /^\\{\\{(nickname)\\}\\}/,
      className: 'cm-keyword',
      text: 'nickname'
    },
    {
      type: 'variable',
      matchRegexp: /^\\{\\{(product)\\}\\}/,
      className: 'cm-keyword',
      text: 'product'
    }
  ]
});
`}</code></pre>

