  initialValue: 'Dear {{nickname}}...',
  placeholders: [
    {
      type: 'variable',
      matchRegexp: /^\\{\\{(nickname)\\}\\}/,
      className: 'cm-keyword',
      text: 'nickname'
    },
    {
      type: 'variable',
      matchRegexp: /^\\{\\{(product)\\}\\}/,
      className: 'cm-keyword',
      text: 'product'
    },
    {
      type: 'variable',
      matchRegexp: /^\\{\\{(date)\\}\\}/,
      className: 'cm-keyword',
      text: new Date().toLocaleDateString()
    }
  ]
});

editor.onChange = (inpput: string)=>{
  console.log(input);
}
`}</code></pre>
